import React from 'react';
import {Link} from 'react-router-dom';

export const RowGenerator = (list, cols) => {

    const rows =[...Array(Math.cell(list.length/cols))];
    const resourceRows = rows.map(
        (row,i) => list.slice(i*cols, i*cols+cols)
    );
        return resourceRows;
}
