import { GET_ERRORS, CLEAR_ERRORS } from "../actions/types";

const initialState = {
  msg:{},
  status: null,
  id: null
};
export default function(state = initialState, action) {
    switch (action.type) {
      case GET_ERRORS:
        return {
          msg: action.paylod.msg,
          status: action.payload.status,
          id: action.payload.id
        }
      case CLEAR_ERRORS:
        return {
          msg: {},
          status: null,
          id: null
        };
      default:
        return state;
    }
  }
  
