import React, {Component} from 'react';
import '../../css/styles.css';
import {Link} from 'react-router-dom';
import {Formik} from 'formik';
import * as Yup from 'yup';


const RegisterSchema = Yup.object().shape({
	firstName: Yup.string().required('Required!!'),
	lastName: Yup.string().required('Required!!'),
	password: Yup.string().min(6,'Too short').required('Required !!'),
	confirmPassword: Yup.string().equals(password).required('Required !!'),
	email: Yup.string().email('Invalid email :(').required('Required !!'),
})
class Register extends Component{

	render(){
	return(
		<div className="log">
			<Formik
				initialValues={{email:'john.doe@sjsu.edu',password:'password123'}}
				validationSchema={RegisterSchema}
				onSubmit={values=>{
					console.log(values)
				}}
			>
				{({
					values,
					errors,
					touched,
					handleChange,
					handleBlur,
					handleSubmit
				})=>(

			
			<form onSubmit={handleSubmit} class="text-center border border-light p-5" action="#!">
    			<p class="h4 mb-4 FormTitle">Sign up</p>

    			<div class="form-row mb-4">
        			<div class="col">
            
            			<input type="text" id="defaultRegisterFormFirstName" class="form-control" placeholder="First name" />
        			</div>
        			<div class="col">
            
            			<input type="text" id="defaultRegisterFormLastName" class="form-control" placeholder="Last name" />
        			</div>
    			</div>

    
    			<input type="email" id="defaultRegisterFormEmail" class="form-control mb-4" placeholder="E-mail" />

    
    			<input type="password" id="defaultRegisterFormPassword" class="form-control" placeholder="Password" aria-describedby="defaultRegisterFormPasswordHelpBlock" />
    			<small id="defaultRegisterFormPasswordHelpBlock" class="form-text text-muted mb-4">
       			 At least 8 characters and 1 digit
    			</small>

    
    			<input type="text" id="defaultRegisterPhonePassword" class="form-control" placeholder="Confirm Password" aria-describedby="defaultRegisterFormPhoneHelpBlock" />
    			<small id="defaultRegisterFormPhoneHelpBlock" class="form-text text-muted mb-4">
       			Re-enter your password
    			</small>

   				<hr/>
    			<button class="btn btn-info my-4 btn-block" type="submit">Submit</button>

    
 

			</form>
				)}
				</Formik>

   </div>



	
	)
}
}

export default Register
