import React, {Component} from 'react';
import '../../css/styles.css';
import {Link, withRouter} from 'react-router-dom';
import {Formik} from 'formik';
import * as Yup from 'yup';
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerUser } from "../../actions/authActions";



const RegisterSchema = Yup.object().shape({
	firstName: Yup.string().required('Required!!'),
	lastName: Yup.string().required('Required!!'),
	email: Yup.string().email('Invalid email :(').required('Required !!'),
	password: Yup.string().min(6,'Too short').required('Required !!'),
	confirmPassword: Yup.string().oneOf([Yup.ref('password'),null],'Passwords must match').required('Required !!'),})
	
	
class Register extends Component{
	constructor() {
		super();
		this.state = {
		  name: "",
		  email: "",
		  password: "",
		  password2: "",
		  errors: {}
		};
	  }
	componentDidMount() {
		// If logged in and user navigates to Register page, should redirect them to dashboard
		if (this.props.auth.isAuthenticated) {
		  this.props.history.push("/WelcomePage");
		}
	  }
	/*
	state = {
		success: false,
		validation:false
	}
	*/
	static getDerivedStateFromProps(props,state){
        const auth = props.user.auth;
        if(auth){
            return {
                success: auth ? true :false
            }
        }
        return null;
    }
	componentWillReceiveProps(nextProps) {
		if (nextProps.errors) {
		  this.setState({
			errors: nextProps.errors
		  });
		}
	  }
	  componentWillReceiveProps(nextProps) {
		if (nextProps.errors) {
		  this.setState({
			errors: nextProps.errors
		  });
		}
	  }
	
	  onChange = e => {
		this.setState({ [e.target.id]: e.target.value });
	  };
	
	  onSubmit = e => {
		e.preventDefault();
	
		const newUser = {
		  firstname: this.state.firstname,
		  lastname: this.state.lastname,
		  email: this.state.email,
		  password: this.state.password,
		  password2: this.state.password2
		};
	
		this.props.registerUser(newUser, this.props.history);
	  };
	
	
	render(){
		const{errors} = this.state;
	return(
		<div className="log">
			{/*<Formik
				initialValues={{firstName:"",lastName:"", email:"", password:"",confirmPassword:""}}
				validationSchema={RegisterSchema}
				onSubmit={values=>{
					this.props.dispatch(registerUser(values)).then(response => {
						if(!this.props.user.auth){
							this.setState({
								validation:true
							})
						}
					})    
				}}
			>
				{({
					values,
					errors,
					touched,
					handleChange,
					handleBlur,
					handleSubmit
				})=>(

				*/}
			<form onSubmit={this.onSubmit} class="text-center border border-light p-5">
    			<p class="h4 mb-4 FormTitle">Sign up</p>

    			<div class="form-row mb-4">
        			<div class="col">
            
            			<input  onChange={this.onChange} value={this.state.firstname} name="first name" type="text" id="defaultRegisterFormFirstName" class="form-control" placeholder="First name" />
						{errors.firstName && touched.firstName ?
				<div className="error_label">{errors.firstName}</div>/*come from Yup*/
		
		:null}
        			</div>
        			<div class="col">
            
            			<input  onChange={this.onChange}  value={this.state.lastname} name="last name" type="text" id="defaultRegisterFormLastName" class="form-control" placeholder="Last name" />
						{errors.lastName && touched.lastName ?
				<div className="error_label">{errors.lastName}</div>/*come from Yup*/
		
		:null}
					</div>
    			<div>

    
    			<input  onChange={this.onChange} value={this.state.email} name="email" type="email" id="defaultRegisterFormEmail" class="form-control mb-4" placeholder="E-mail" />
				{errors.email && touched.email?
				<div className="error_label">{errors.email}</div>/*come from Yup*/
		
		:null}
				</div>
				<div></div>
    			<input  onChange={this.onChange}  value={this.state.password} name="password" type="password" id="defaultRegisterFormPassword" class="form-control" placeholder="Password" aria-describedby="defaultRegisterFormPasswordHelpBlock" />
    			<small id="defaultRegisterFormPasswordHelpBlock" class="form-text text-muted mb-4">
       			 At least 8 characters and 1 digit
    			</small>
				{errors.password && touched.password ?
				<div className="error_label">{errors.password}</div>/*come from Yup*/
		
		:null}
					</div>
				<div>
    			<input onChange={this.onChange}  value={this.state.password2} name="confirm password" type="password" id="defaultRegisterPhonePassword" class="form-control" placeholder="Confirm Password" aria-describedby="defaultRegisterFormPhoneHelpBlock" />
    			<small id="defaultRegisterFormPhoneHelpBlock" class="form-text text-muted mb-4">
       			Re-enter your password
    			</small>
				{errors.confirmPassword && touched.confirmPassword ?
				<div className="error_label">{errors.confirmPassword}</div>/*come from Yup*/
		
		:null}
					</div>
   				<hr/>
    			<button class="btn btn-info my-4 btn-block" type="submit">Submit</button>

    
 

			</form>
				
				{/*</Formik>*/}

   </div>



	
	)
}
}

Register.propTypes = {
	registerUser: PropTypes.func.isRequired,
	auth: PropTypes.object.isRequired,
	errors: PropTypes.object.isRequired
  };
  const mapStateToProps = state => ({
	auth: state.auth,
	errors: state.errors
  });
  export default connect(
	mapStateToProps,
	{ registerUser }
  )(withRouter(Register));
