      return (
      <Resource  key={resource._id}
        title={resource.title}
       content={resource.content}
       rating={resource.rating}
       />
    )
      })
      
  )
*/
  return(
    <div>
      <div>
<Navbaruser />
</div>
    <div>
    <Container  style={{
               paddingTop: "20px"
               
              }} >
      <Row>
      
        {resources.map((res)=>(
          <Resource
          key={res._id}
          id={res._id}
          title={res.title}
          content={res.content}
          category={res.category}
          image={res.image}
          rating={res.rating}

            
         
          />
        ))}
     </Row>
    </Container>
    </div>
      </div>  
          
          
        
      
      
    
  )
      }

const mapStateToProps = (state) => {
  
  return {
    resource: state.resource,
  };
};
