import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import Navbaruser from "../Layout/NavbarUser";
import {BrowserRouter} from 'react-router-dom';
import {getResources} from '../../actions/resourceAction';

class Home extends Component {
  /*
  static propTypes={
    logout: PropTypes.func.isRequired
  }
  */
  componentDidMount(){
    this.props.dispatch(getResources(6,0,'desc'));
  }
  onLogoutClick = e => {
    e.preventDefault();
    this.props.logoutUser();
  };
  
  render() {
     console.log(this.props)
    const {resources} = this.props.resource;
    const { user } = this.props.auth;
    return (
     
      <BrowserRouter>
      
      
      <Navbaruser />
      <div style={{ height: "75vh" }} >
        Wecome to the homepage!
       

            <button
              style={{
                width: "150px",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "1rem"
              }}
              onClick={this.onLogoutClick}
              href="#"
              
            >
              Logout
            </button>
    
     
       
          </div>
           
     </BrowserRouter>
    );
  }
}

Home.propTypes = {
  getResources: PropTypes.func.isRequired,
  resource: PropTypes.object.isRequired,
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
  resource: state.resource, //same as the one in rootReducer
  auth: state.auth,
  getResources: state.resource
});
export default connect(
  mapStateToProps,
  { logoutUser }
)(Home);
/*
export default connect(
  null,
  {logoutUser}
)(Home);
*/
