import axios from 'axios';
import {GET_RESOURCES,GET_RESOURCE,RESOURCES_LOADING} from '../actions/types';
import {returnErrors} from "./errorAction"

export function getPopularResources(
    
    order = 'asc',
    list
){
    
      
    const request = axios.get(`/api/resources/popular?order=${order}`)
                    .then (response =>{
                        return list ? [...list,...response.data] : response.data;
                    });
                   
                    return {

                        type: GET_RESOURCES,
                        payload: request
                    }
}


export function getResource(resourceId){
    //  /api/resources/resource?id=5e13765a4084511885f252f1
    
    const request = axios.get(`/api/resources/resource?id=${resourceId}`)
                 .then( response => {
                     return response.data
                 }).catch((err)=>{
                     return false
                 })
 
     return {
         type: GET_RESOURCE,
         payload: request
     }
 
 }
 

 export const setResourcesLoading = () => {
    return {
      type: RESOURCES_LOADING
    };
  };
