import React, {Component} from 'react';
import '../../css/styles.css';
import {Link, withRouter} from 'react-router-dom';
import {Formik} from 'formik';
import * as Yup from 'yup';


const RegisterSchema = Yup.object().shape({
	firstName: Yup.string().required('Required!!'),
	lastName: Yup.string().required('Required!!'),
	email: Yup.string().email('Invalid email :(').required('Required !!'),
	password: Yup.string().min(6,'Too short').required('Required !!'),
	confirmPassword: Yup.string().oneOf([Yup.ref('password'),null],'Passwords must match').required('Required !!'),})
	
class Register extends Component{
	state = {
		success: false,
		validation:false
	}
	render(){
	return(
		<div className="log">
			<Formik
				initialValues={{firstName:'Natty',lastName:'Khat', email:'Natty.Khat@sjsu.edu', password:'password123',confirmPassword:'password123'}}
				validationSchema={RegisterSchema}
				onSubmit={values=>{
					console.log(values)
				}}
			>
				{({
					values,
					errors,
					touched,
					handleChange,
					handleBlur,
					handleSubmit
				})=>(

			
			<form onSubmit={handleSubmit} class="text-center border border-light p-5" action="#!">
    			<p class="h4 mb-4 FormTitle">Sign up</p>

    			<div class="form-row mb-4">
        			<div class="col">
            
            			<input  onChange={handleChange} onBlur={handleBlur} value={values.firstName} name="first name" type="text" id="defaultRegisterFormFirstName" class="form-control" placeholder="First name" />
						{errors.firstName && touched.firstName ?
				<div className="error_label">{errors.firstName}</div>/*come from Yup*/
		
		:null}
        			</div>
        			<div class="col">
            
            			<input  onChange={handleChange} onBlur={handleBlur} value={values.lastName} name="last name" type="text" id="defaultRegisterFormLastName" class="form-control" placeholder="Last name" />
        			</div>
    			</div>

    
    			<input  onChange={handleChange} onBlur={handleBlur} value={values.email} name="email" type="email" id="defaultRegisterFormEmail" class="form-control mb-4" placeholder="E-mail" />

    
    			<input  onChange={handleChange} onBlur={handleBlur} value={values.password} name="password" type="password" id="defaultRegisterFormPassword" class="form-control" placeholder="Password" aria-describedby="defaultRegisterFormPasswordHelpBlock" />
    			<small id="defaultRegisterFormPasswordHelpBlock" class="form-text text-muted mb-4">
       			 At least 8 characters and 1 digit
    			</small>

    
    			<input onChange={handleChange} onBlur={handleBlur} value={values.confirmPassword} name="confirm password" type="text" id="defaultRegisterPhonePassword" class="form-control" placeholder="Confirm Password" aria-describedby="defaultRegisterFormPhoneHelpBlock" />
    			<small id="defaultRegisterFormPhoneHelpBlock" class="form-text text-muted mb-4">
       			Re-enter your password
    			</small>

   				<hr/>
    			<button class="btn btn-info my-4 btn-block" type="submit">Submit</button>

    
 

			</form>
				)}
				</Formik>

   </div>



	
	)
}
}

export default Register
