import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import Navbaruser from "../Layout/NavbarUser";
import {BrowserRouter} from 'react-router-dom';
import {fetchAllPosts, getPopularResources} from '../../actions/resourceAction';
import {RowGenerator, GenerateRowsWithBlocks} from '../../utils/helpers'
import  '../../css/styles.css'
import Resource from './resource'

const Resources = ({ fetchAllPosts, resources, loading, error }) => {
 

  const noPostsMessage = "There are no posts to be fetched";

  const renderPosts =
   
      this.props.resources.map((resource) => {
        return (
          <Resource
            key={resource._id}
            id={resource._id}
            title={resource.author}
            content={resource.content}
            category={resource.createdAt}
            rating={resource.updatedAt}
          />
        );
      })
    

  return (
    <div>
     
        <div>{renderPosts}</div>
      
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    loading: state.loading,
    resources: state.resources,
    error: state.error,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchAllPosts: () => dispatch(fetchAllPosts()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Resources);

