import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import Navbaruser from "../Layout/NavbarUser";
import {BrowserRouter} from 'react-router-dom';
import {getPopularResources} from '../../actions/resourceAction';
import {RowGenerator, GenerateRowsWithBlocks} from '../../utils/helpers'
import  '../../css/styles.css'

import ReactTable from 'react-table';
import styled from 'styled-components'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`
  
class Home extends Component {
  constructor(props) {
    super(props)
    this.state = {
        resources: [],
        columns: [],
        isLoading: false,
    }
}

componentDidMount = async () => {
    this.setState({ isLoading: true })

    await api.getPopularResources().then(resources => {
        this.setState({
            resources: resources.data.data,
            isLoading: false,
        })
    })
}

  /*
  static propTypes={
    logout: PropTypes.func.isRequired
  }
  */
 /*
  componentDidMount(){
    this.props.dispatch(getResources(6,0,'desc'));
  }
  */
