        return generatedResources;
      }
    return false;
  }
  */
  onLogoutClick = e => {
    e.preventDefault();
    this.props.logoutUser();
  };
/*  
  componentDidMount(){
    this.props.dispatch(getPopularResources())
}
*/
/*
loadmore = () => {
    let resourceList = this.props.resources.collection;
    //let count = resourceList.length;
    this.props.dispatch(getPopularResources('desc',resourceList))
}


showResources = (resources) => {
    if(resources){
        const rowsArray = RowGenerator(resources, 2);
        const generatedResources = GenerateRowsWithBlocks(rowsArray,'six')
        return generatedResources;
    }
    return false;
}
*/

  render() {
     //console.log(this.props.resources)
    //const {resources} = this.props.resources;
    const { resources, isLoading } = this.state
    console.log('TCL: ResourcesList -> render -> resources', rescources)

    const columns = [
      {
          Header: 'ID',
          accessor: '_id',
          filterable: true,
      },
      {
          Header: 'Title',
          accessor: 'title',
          filterable: true,
      },
      {
          Header: 'Rating',
          accessor: 'rating',
          filterable: true,
      },
      
  ]

  let showTable = true
  if (!resources.length) {
      showTable = false
  }
    return (
     
    <div>
      
      
      <Navbaruser />
      <div style={{ height: "75vh" }} >
        Wecome to the homepage!
       

            <button
              style={{
                width: "150px",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "1rem"
              }}
              onClick={this.onLogoutClick}
              href="#"
              
            >
              Logout
            </button>
              
             {/*this.showResources(this.props.resources)}*/}
     
       
         
            </div>

          
          </div>
        
     
    );
  }
}

Home.propTypes = {
  //getResources: PropTypes.func.isRequired,
  //resource: PropTypes.object.isRequired,
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired

};
const mapStateToProps = state => ({
 // resources: state.resources, //same as the one in rootReducer
  auth: state.auth,
  resources: state.resources
  
});
export default connect(
  mapStateToProps,
  { logoutUser, getPopularResources }
)(Home);
