import React from "react";
import { Card, CardImg,  CardHeader, CardBody, CardText,Row,Col, Button } from "reactstrap";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
const Resource = ({
  title,
  content,
  category, image,
  rating,resource}) => {
      return(
      
    <div className="col mb-4">
     
      
      <Card className="h-80">
        <CardHeader>
          {title}
        </CardHeader>
        
        <CardBody >
        <div class="view overlay">
      <img class="card-img-top" src={image}
        alt="Card image cap"/>
      <a href="#!">
        <div class="mask rgba-white-slight"></div>
      </a>
    </div>
        <CardText>
            {content}
          </CardText>
          <CardText>
            {category.slug}
          </CardText>
          <CardText>{rating}</CardText>
          <a href="#!" class="blue-text d-flex justify-content-end">
      <h5>Read more <i class="fas fa-angle-double-right"></i></h5>
    </a>

         
        </CardBody>
      </Card>
    
     
    </div>
  
      )

};
/*
const mapStateToProps = (state) => {
  return {
    resource: state.resource,
  };
};
*/
/*
const mapDispatchToProps = (dispatch) => {
  return {
    deletePost: (id) => dispatch(deletePost(id)),
    getPostDate: (id) => dispatch(getPostDate(id)),
  };
};
*/
const mapStateToProps = (state) => {
  return {
    post: state.post,
  };
};
export default connect(mapStateToProps)(Resource);
