import {GET_RESOURCES} from '../actions/types';

export const getResources = () => {
    limit = 50,
    start = 0,
    order = 'asc',
    list

    const request = axios.get(`/api/resources/popular?limit=${limit}&skip=${start}&order=${order}`)
                    .then (response =>{
                        return response.data
                    });
                   
                    return {

