import React, { Component, useEffect } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import Navbaruser from "../Layout/NavbarUser";
import {BrowserRouter} from 'react-router-dom';
import { getPopularResources} from '../../actions/resourceAction';
import {RowGenerator, GenerateRowsWithBlocks} from '../../utils/helpers'
import  '../../css/styles.css'
import Resource from '../Layout/Resource'
import {Container, ListGroup, ListGroupItem} from 'reactstrap';
 

const Home = ({getPopularResources, resource
}) => {

  useEffect(()=>{
    getPopularResources();
  },[getPopularResources]);

//const noPostsMessage = "There are no posts to be fetched";
//const resarr = Array.from(resource)
//console.log(resarr[1])
const {resources} = resource
/*
const renderPosts =

  resources.length === 0 ? (
    <Alert color = "primary">{noPostsMessage}</Alert>
  ):(
  resources.resource.map((resource)=>{
      return (
      <Resource  key={resource._id}
        title={resource.title}
       content={resource.content}
       rating={resource.rating}
       />
    )
      })
      
  )
*/
  return(
    <Container>
      <div>
      <Navbar color="light" light expand="md">
        <NavbarBrand href="/">app</NavbarBrand>
        <NavbarToggler onClick={toggle} />
        <Collapse isOpen={isOpen} navbar>
          <Nav className="mr-auto" navbar>
            <NavItem>
              <NavLink href="/foo/">foo</NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="https://example.com">example</NavLink>
            </NavItem>
            <UncontrolledDropdown nav inNavbar>
              <DropdownToggle nav caret>
                options
              </DropdownToggle>
              <DropdownMenu right>
                <DropdownItem>option 1</DropdownItem>
                <DropdownItem>option 2</DropdownItem>
                <DropdownItem divider />
                <DropdownItem>reset</DropdownItem>
              </DropdownMenu>
            </UncontrolledDropdown>
          </Nav>
          <NavbarText>more text</NavbarText>
        </Collapse>
      </Navbar>
    </div>
      <ListGroup>
        {resources.map(({_id, title,content})=>(
          <ListGroupItem>
            {title}
            <div>
               {content}
            </div>
           
          </ListGroupItem>
        ))}
      </ListGroup>
    </Container>
        
          
          
        
      
      
    
  )
      }

const mapStateToProps = (state) => {
  
  return {
    resource: state.resource,
  };
};
