import React, { Component, useEffect,useState } from "react";
import PropTypes from "prop-types";
import { connect, useSelector, useDispatch } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import Navbaruser from "../Layout/NavbarUser";
import {BrowserRouter} from 'react-router-dom';
import { getResourcesByCount, fetchResourcesByFilter, getCategories} from '../../actions/resourceAction';
import {RowGenerator, GenerateRowsWithBlocks} from '../../utils/helpers'
import  '../../css/styles.css'
import ResourceCard from '../Layout/ResourceCard'
import {Container,Row} from 'reactstrap';
import Search from '../Features/Search';
import Star from "../Features/Star"
import {Menu, Checkbox} from "antd";
import {DownCircleOutlined, DownSquareOutlined, StarOutlined} from '@ant-design/icons'
import { SEARCH_QUERY } from '../../actions/types'

const {SubMenu, ItemGroup} = Menu;

const Home = () => {

const [resources, setResources] = useState([])

const[loading, setLoading] = useState(false)
const [categories, setCategories] = useState([])
const [categoryIds, setCategoryIds] = useState([])
const [star, setStar] = useState('')

useEffect(() => {

  loadAllResources()
})
const loadAllResources =() => {
  getResourcesByCount(4).then((r)=>{
    setResources(r.data)
    setLoading(false)
  }) 
}
/*
  useEffect(()=>{
    getResourcesByCount(4);
  },[getResourcesByCount]);
*/
  let dispatch = useDispatch()
  let {search} = useSelector((state) => ({ ...state}));
  const {text } = search;

//const {resources} = resource

useEffect(()=>{
  getResourcesByCount(4);
  getCategories().then((res) =>
  setCategories(res.data)
  )

}, [])

   
useEffect(() => {
  const delayed = setTimeout(() =>{
fetchResources({query:text})

  },300) 
  console.log(text)
  return () => clearTimeout(delayed)
  
  
},[text])

const fetchResources = (arg) => {
  fetchResourcesByFilter(arg).then(res => {
    setResources(res.data)
  })
}
const showCategories = () => categories.map((c) =>(
<div key={c._id}>
  <Checkbox onChange={handleCheck} className="pb-2 pl-4 pr-4"  style={{
               paddingTop: "20px"
               
              }} value={c._id} name="category" checked={categoryIds.includes(c._id)}>
  
    {c.name} 
    <br />
  </Checkbox>
  <br />
  </div>
))
const handleStarClick = (num) => {
console.log(num)
}
const showStars = () => (
  <div className="pr-4 pl-4 pb-2">

  <Star starClick={handleStarClick} numberOfStars={5} />
  </div>

)
const handleCheck = e => {
  dispatch({
    type: SEARCH_QUERY,
    payload: {text: ""}
  })
//console.log(e.target.value)
let inTheState = [...categoryIds];
let justChecked = e.target.value;
let foundInTheState = inTheState.indexOf(justChecked)
if(foundInTheState === -1) {
  inTheState.push(justChecked)
}else{
  inTheState.splice(foundInTheState, 1)

}
setCategoryIds(inTheState)
fetchResources({category: inTheState})
}
  return(
    <div>
      <div>
<Navbaruser />
</div>
<div className= "col-md-3">
        search/filter menu
      </div>
<span className="float">
      <Search />
      </span>

<div className="container-fluid">
  <div className="row">
      
      <div className="col-md-9">
        {loading? (
          <h4 className="text-danger"> Loading...</h4>
        ) : (
          <h4 className="text-danger">Resources</h4>
        ) }

        {resources.length < 1 && <p>No products found</p>}
        <div className='row'>
          {resources.map((r)=> (
          <div  className="col-md-4">
            <ResourceCard 
            key={r._id}
            id={r._id}
          title={r.title}
          content={r.content}
          image={r.image.image}
          category={r.category.slug}
          rating={r.rating}
 />
        </div>
        ))}
      </div>
  </div>
</div>
</div>

{/*
<div>
    <span className="float-right p-1">
      <Search />
      </span>
    </div>
    <div>
    <div className="container-fluid" style={{
               paddingTop: "20px"
               
              }} >
      <Row>
        <div className="col-md-3">
          <h4>search/filter menu</h4>
          <Menu defaultOpenKeys={["1","2"]}  >
            <SubMenu key="1" title={
              <span className="h6" >
                
                categories
                <DownSquareOutlined />
              </span>
