		if (this.props.auth.isAuthenticated) {
		  this.props.history.push("/home");
		}
	  }
	
	componentWillReceiveProps(nextProps) {
		if (nextProps.auth.isAuthenticated) {
		  this.props.history.push("/home"); // push user to dashboard when they login
		}
		if (nextProps.errors) {
			this.setState({
			  errors: nextProps.errors
			});
		  }
		}
		onChange = e => {
			this.setState({ [e.target.id]: e.target.value });
		  };
		
		  onSubmit = e => {
			e.preventDefault();
		
			const userData = {
			  email: this.state.email,
			  password: this.state.password
			};
		
			this.props.loginUser(userData);
		  };
		  */
		 state ={
			success: false,
			validation:false
		}
	
		static getDerivedStateFromProps(props,state){
			const auth = props.auth;
			if(auth){
				return {
					success: auth ? true :false
				}
			}
			return null;
		}
	
	
		componentDidUpdate(){
			if(this.state.success){
				this.props.history.push('/home');
			}
		}
	
	render(){
		//const { errors } = this.state;

		return(
			<div className="log">

		<Formik
                   initialValues={{ email:'francis@gmail.com', password:'password123'}}
                    validationSchema={LoginSchema}
                    onSubmit={ values => {
                        this.props.dispatch(loginUser(values)).then(response => {
                            if(!this.props.user.auth){
                                this.setState({
                                    validation:true
                                })
                            }
                        })                       
                    }}
                >
                    {({
                      values,
                      errors,
                      touched,
                      handleChange,
                      handleBlur,
                      handleSubmit  
                    })=>(
			
				
				<form onSubmit={handleSubmit}  className="text-center border border-light p-5" action="#!">{/*onSubmit={this.onSubmit}*/}

    			<p className="h4 mb-4 FormTitle">Sign in</p>

   		 	<input name="email" type="email"  class="form-control mb-4" placeholder="E-mail" onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.email} />
										 { errors.email && touched.email ?
                                        <div className="error_label">{errors.email}</div>
                                    :null}
										 {/*onChange={this.onChange}  value={this.state.email} error={errors.email} id="email"/>*/}
			
 
    		<input type="password" name="password"  class="form-control mb-4" placeholder="Password"   onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.password} >
											{ errors.password && touched.password ?
                                        <div className="error_label">{errors.password}</div>
                                    :null}
											</input> {/*onChange={this.onChange}  value={this.state.password} error={errors.password} id="password"/>*/}
			
    
       
            <a href="">Forgot password?</a>
        

  
    	<button class="btn btn-info btn-block my-4" type="submit">Sign in</button>

    
    	<p>Not a member? Please register below.
       
    	</p>

    	<button class="btn btn-info btn-block my-4" type="submit">Register</button>
   

		</form>

				
)}
</Formik>
			
	<Link to={{
				pathname: `${this.props.match.url}/Register`
			}}></Link>
</div>


			)
	}
}

Login.propTypes = {
	loginUser: PropTypes.func.isRequired,
	auth: PropTypes.object.isRequired,
	errors: PropTypes.object.isRequired
  };
  const mapStateToProps = state => ({
	auth: state.auth,
	errors: state.errors
  });
  export default connect(
	mapStateToProps,
	{ loginUser }
  )(withRouter(Login));

