import uuid from 'uuid';
import {GET_RESOURCES} from '../actions/types';

const initialState = {

    resources:[
    ]
    
};
export default function(state = initialState, action) {

    switch(action.type){
        case GET_RESOURCES:
            return {
                ...state, collection: action.payload
            }
        default:
            return state;

    }

}
