/*
const initialState = {

    resources:[
    ]
    
};
export default function(state = initialState, action) {

    switch(action.type){
        case GET_RESOURCES:
            return {
                ...state, collection: action.payload
            }
        default:
            return state;

    }

}
*/
import {
    
    GET_RESOURCES,
    GET_RESOURCE
} from '../../actions/types';


export default function(state={},action){
    switch(action.type){
       
        case GET_RESOURCE:
            return {...state, single: action.payload }
       
        case GET_RESOURCES:
            return {...state, collection: action.payload }
        default:
            return state;
    }
}
