import React, { Component, useEffect,useState } from "react";
import PropTypes from "prop-types";
import { connect, useSelector, useDispatch } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import Navbaruser from "../Layout/NavbarUser";
import {BrowserRouter} from 'react-router-dom';
import { getResourcesByCount, fetchResourcesByFilter, getCategories} from '../../actions/resourceAction';
import {RowGenerator, GenerateRowsWithBlocks} from '../../utils/helpers'
import  '../../css/styles.css'
import Resource from '../Layout/Resource'
import {Container,Row} from 'reactstrap';
import Search from '../Features/Search'
import {Menu, Checkbox} from "antd";
import {DownCircleOutlined, DownSquareOutlined} from '@ant-design/icons'
 
const {SubMenu, ItemGroup} = Menu;

const Home = ({getResourcesByCount,resource
}) => {
const [setResources] = useState([]);
const[loading, setLoading] = useState(false)
const [categories, setCategories] = useState([])

  useEffect(()=>{
    getResourcesByCount(4);
  },[getResourcesByCount]);

  let {search} = useSelector((state) => ({ ...state}));
  const {text } = search;

const {resources} = resource

useEffect(()=>{
  getResourcesByCount(4);
  getCategories().then((res) =>
  setCategories(res.data)
  )

}, [])
/*
const renderPosts =

  resources.length === 0 ? (
    <Alert color = "primary">{noPostsMessage}</Alert>
  ):(
  resources.resource.map((resource)=>{
      return (
      <Resource  key={resource._id}
        title={resource.title}
       content={resource.content}
       rating={resource.rating}
       />
    )
      })
      
  )
*/
useEffect(() => {
  const delayed = setTimeout(() =>{
fetchResources({query:text})

  },300) 
  console.log(text)
  return () => clearTimeout(delayed)
  
  
},[text])

const fetchResources = (arg) => {
  fetchResourcesByFilter(arg).then(res => {
    setResources(res.data)
  })
}
const showCategories = () => categories.map((c) =>
<div key={c._id}>
  <Checkbox className="pb-2 pl-4 pr-4" value={c._id} name="category">
    {c.name}
  </Checkbox>
  <br />
  </div>
)

  return(
    <div>
      <div>
<Navbaruser />
</div>
<div>
    <span className="float-right p-1">
      <Search />
      </span>
    </div>
    <div>
    <div className="container-fluid" style={{
               paddingTop: "20px"
               
              }} >
      <Row>
        <div className="col-md-3">
          <h4>search/filter menu</h4>
          <Menu defaultOpenKeys={["1","2"]}  >
            <SubMenu key="1" title={
              <span className="h6">
                
                categories
                <DownSquareOutlined />
              </span>
            }>
              <div style={{maringTop: "-10px"}}>
              {JSON.stringify(categories)}

              </div>
            </SubMenu>
            
          </Menu>
          
        </div>
        
      <div className="col-md-9">
        {resources.length < 1 && <p>No resources found</p>}
        <div className="row">
        {resources.map((res)=>(
          <Resource
          key={res._id}
          id={res._id}
          title={res.title}
          content={res.content}
          category={res.category.slug}
          image={res.image.image}
          rating={res.rating}

            
         
          />
        ))}
        </div>
        </div>
     </Row>
    </div>
    </div>
        
      </div>  
          
          
        
      
      
    
  )
      }

const mapStateToProps = (state) => {
  
  return {
    resource: state.resource,
  };
};
/*
const mapDispatchToProps = (dispatch) => {
  return {
    getPopularResources: () => dispatch(getPopularResources()),
  };
};
*/

export default connect(mapStateToProps,{getResourcesByCount})(Home);
